pragma solidity 0.5.8;
pragma experimental ABIEncoderV2;

library SafeMath {

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }

        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");

        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b > 0, "SafeMath: division by zero");
        uint256 c = a / b;

        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b <= a, "SafeMath: subtraction overflow");
        uint256 c = a - b;

        return c;
    }

    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");

        return c;
    }
}

interface IERC20 {
    function transfer(address to, uint256 value) external returns (bool);
    function approve(address spender, uint256 value) external returns (bool);
    function transferFrom(address from, address to, uint256 value) external returns (bool);
    function totalSupply() external view returns (uint256);
    function limitSupply() external view returns (uint256);
    function availableSupply() external view returns (uint256);
    function balanceOf(address who) external view returns (uint256);
    function allowance(address owner, address spender) external view returns (uint256);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contract ERC20 is IERC20 {
    using SafeMath for uint256;
    //address busd = 0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56; // live busd
    //address busd = 0x7F4edc9AC9d9fbFdBEB9B6deb81d26a833B11476; // testnet busd
    address busd = 0xf9675Fb5Ab89D8d2e825b43E07c518C577E65fb0; // Ganache
    IERC20 token;
    mapping (address => uint256) private _balances;

    mapping (address => mapping (address => uint256)) private _allowances;

    uint256 private _totalSupply;
    uint256 internal _limitSupply;

    string internal _name;
    string internal _symbol;
    uint8 internal _decimals;

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function limitSupply() public view returns (uint256) {
        return _limitSupply;
    }
    
    function availableSupply() public view returns (uint256) {
        return _limitSupply.sub(_totalSupply);
    }    

    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].sub(subtractedValue));
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) internal {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _balances[sender] = _balances[sender].sub(amount);
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: mint to the zero address");
        require(availableSupply() >= amount, "Supply exceed");

        _totalSupply = _totalSupply.add(amount);
        
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: burn from the zero address");

        _balances[account] = _balances[account].sub(amount);
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

}

contract ApproveAndCallFallBack {
    function receiveApproval(address from, uint256 amount, address token, bytes calldata extraData) external;
}

contract Token is ERC20 {
    mapping (address => bool) private _contracts;

    constructor() public {
        _name = "Coin Factory";
        _symbol = "CFY";
        _decimals = 18;
        _limitSupply = 100000e18;
        
    }

    function approveAndCall(address spender, uint256 amount, bytes memory extraData) public returns (bool) {
        require(approve(spender, amount));

        ApproveAndCallFallBack(spender).receiveApproval(msg.sender, amount, address(this), extraData);

        return true;
    }

    

    function transfer(address to, uint256 value) public returns (bool) {

        if (_contracts[to]) {
            approveAndCall(to, value, new bytes(0));
        } else {
            super.transfer(to, value);
        }

        return true;
    }
}

contract BUSDMaker is Token {
    
    uint private startTime = 1111; 
    
    address payable private ADMIN;
    address payable private DEV_1;
    
    uint public totalUsers; 
    uint public totalBUSDStaked; 
    uint public totalTokenStaked;    

    uint private constant ADV_FEE           = 15;    
    uint private constant LIMIT_TOKEN_PRICE = 1 ether;
    uint private constant LIMIT_AIRDROP     = 10000 ether;  
    uint private constant BUSD_DAILYPROFIT  = 20;
    uint private constant PERCENT_DIVIDER   = 1000;
    uint private constant PRICE_DIVIDER     = 1 ether;
    uint private constant TIME_STEP         = 1 days;
    uint private constant TIME_TO_UNSTAKE_E   = 0 days;
    uint private constant TIME_TO_UNSTAKE_M   = 1 days;
    uint private constant TIME_TO_UNSTAKE_H   = 2 days;
    uint private constant TOKEN_DAILYPROFIT_E = 30;
    uint private constant TOKEN_DAILYPROFIT_M = 40;
    uint private constant TOKEN_DAILYPROFIT_H = 50;
    uint private constant NEXT_AIRDROP      = 7 days;
    uint private constant SELL_LIMIT        = 60000 ether; 
    uint private constant SELL_LIMIT_DAILY_USER        = 10000 ether; 

    
    mapping(address => User) private users;
    mapping(uint => uint) private sold; 
    enum StakeType {EASY, MEDIUM, HARD}
    
    struct Stake {
        uint checkpoint;
        uint totalStaked; 
        uint lastStakeTime;
        uint unClaimedTokens;     
    }

    struct Staking{
        uint id;
        address addr;
        uint amount;
        StakeType period;
        uint startTimeStake;
    }
        Staking[] public stakings;

    
    struct User {
        address referrer;
        uint lastAirdrop;
        Stake sM;
        Stake sT;  
		uint256 bonus;
		uint256 totalBonus;
        uint totaReferralBonus;
        uint dailySell;
        uint lastTimeSold;
    }

    
    
    event TokenOperation(address indexed account, string txType, uint tokenAmount, uint trxAmount);

    constructor() public {
        token = IERC20(busd);

        ADMIN = msg.sender;
        DEV_1 = msg.sender;
 

        _mint(msg.sender, LIMIT_AIRDROP.mul(5));  
    }       
    
    modifier onlyOwner {
        require(msg.sender == ADMIN, "Only owner can call this function");
        _;
    } 
    
    function stakeBUSD(address referrer,  uint256 _amount) public payable {
        require (block.timestamp > 1639832400);
        token.transferFrom(msg.sender, address(this), _amount);
        
		uint fee = _amount.mul(ADV_FEE).div(PERCENT_DIVIDER); 
        token.transfer(DEV_1, fee);
        
		User storage user = users[msg.sender];

        if (msg.sender != ADMIN && referrer != ADMIN) {
        user.referrer = referrer;
        token.transfer(referrer, fee); 
        }

        if (user.sM.totalStaked == 0) {
            user.sM.checkpoint = maxVal(now, startTime);
            totalUsers++;
        } else {
            updateStakeBUSD_IP(msg.sender);
        }
        user.sM.lastStakeTime = now;
        user.sM.totalStaked = user.sM.totalStaked.add(_amount);
        totalBUSDStaked = totalBUSDStaked.add(_amount); 
    }

    
    
    function stakeToken(uint tokenAmount, uint _periodLocked) public {
        User storage user = users[msg.sender];

        require(now >= startTime, "Stake not available yet");
        require(tokenAmount <= balanceOf(msg.sender), "Insufficient Token Balance");

        if (user.sT.totalStaked == 0) {
            user.sT.checkpoint = now;
        } else {
            updateStakeToken_IP(msg.sender);
        }
        
        _transfer(msg.sender, address(this), tokenAmount);
        user.sT.lastStakeTime = now;

        user.sT.totalStaked = user.sT.totalStaked.add(tokenAmount);
        totalTokenStaked = totalTokenStaked.add(tokenAmount); 
        uint amountArray = countStakeArray();
        addStake((amountArray+1), msg.sender, tokenAmount, _periodLocked, now);
    
    } 
    
    function unStakeToken(uint _id) public {
        User storage user = users[msg.sender];
        updateStakeToken_IP(msg.sender);
        require(checkStakeTime(_id), "You cannot unstake your tokens yet");
        uint tokenAmount = stakings[_id].amount;
        delete stakings[_id];
        user.sT.totalStaked = user.sT.totalStaked-tokenAmount;
        totalTokenStaked = totalTokenStaked.sub(tokenAmount); 
        _transfer(address(this), msg.sender, tokenAmount);
    }  
    
    function updateStakeBUSD_IP(address _addr) private {
        User storage user = users[_addr];
        uint256 amount = getStakeBUSD_IP(_addr);
        if(amount > 0) {
            user.sM.unClaimedTokens = user.sM.unClaimedTokens.add(amount);
            user.sM.checkpoint = now;
        }
    } 

    
    
    function getStakeBUSD_IP(address _addr) view private returns(uint256 value) {
        User storage user = users[_addr];
        uint256 fr = user.sM.checkpoint;
        if (startTime > now) {
          fr = now; 
        }
        uint256 Tarif = BUSD_DAILYPROFIT;
        uint256 to = now;
        if(fr < to) {
            value = user.sM.totalStaked.mul(to - fr).mul(Tarif).div(TIME_STEP).div(PERCENT_DIVIDER);
        } else {
            value = 0;
        }
        return value;
    }  
    
    function updateStakeToken_IP(address _addr) private {
        User storage user = users[_addr];
        uint256 amount = getStakeToken_IP(_addr);
        if(amount > 0) {
            user.sT.unClaimedTokens = user.sT.unClaimedTokens.add(amount);
            user.sT.checkpoint = now;
        }
    } 


    function countStakeArray() view private returns(uint value) {
        value = stakings.length;
        if(value < 0){
        value = 0;
        }
        return value;
    }


    
    
    function getStakeToken_IP(address _addr) view private returns(uint256 value) {
        User storage user = users[_addr];
        uint256 fr = user.sT.checkpoint;
        if (startTime > now) {
          fr = now; 
        }
        uint256 amountArr = countStakeArray();
        uint256 to = now;
        uint256 valueCal;
        if(fr < to) {
            for(uint256 i = 0; i < amountArr; i++){
                if (stakings[i].addr == _addr && (getUserTokensAccumulated().mul(2) < getUserBUSDStaked(msg.sender) || getTokenPrice() < LIMIT_TOKEN_PRICE)) {
                    if(stakings[i].period == StakeType.EASY){
                    valueCal = stakings[i].amount.mul(to - fr).mul(TOKEN_DAILYPROFIT_E).div(TIME_STEP);
                    value += valueCal;
                    }else  if(stakings[i].period == StakeType.MEDIUM){
                    valueCal = stakings[i].amount.mul(to - fr).mul(TOKEN_DAILYPROFIT_M).div(TIME_STEP);
                    value += valueCal;
                    }else if(stakings[i].period == StakeType.HARD){
                    valueCal = stakings[i].amount.mul(to - fr).mul(TOKEN_DAILYPROFIT_H).div(TIME_STEP);
                    value += valueCal;
                    }
                }
            }
            value = value.div(PERCENT_DIVIDER);
        } else {
            value = 0;
        }
        return value;
    }   

    function getStakeTokenReward(address _addr, uint _id, uint _daily) public view returns(uint256 value){
        if(stakings[_id].addr == _addr){
             uint256 to = now;
            uint256 fr = stakings[_id].startTimeStake;
            value = stakings[_id].amount.mul(to - fr).mul(_daily).div(TIME_STEP).div(PERCENT_DIVIDER);
        } 

        return value;
    }   
    
    function claimToken_M() public {
        User storage user = users[msg.sender];
       
        updateStakeBUSD_IP(msg.sender);
        uint tokenAmount = user.sM.unClaimedTokens;  
        user.sM.unClaimedTokens = 0;                 
        
        _mint(msg.sender, tokenAmount);
        emit TokenOperation(msg.sender, "CLAIM", tokenAmount, 0);
    }    
    
    function claimToken_T() public {
        User storage user = users[msg.sender];
       
        updateStakeToken_IP(msg.sender);
        uint tokenAmount = user.sT.unClaimedTokens; 
        user.sT.unClaimedTokens = 0; 
        
        _mint(msg.sender, tokenAmount);
        emit TokenOperation(msg.sender, "CLAIM", tokenAmount, 0);
    }     
    
    

    function sellToken(uint tokenAmount) public {
        uint amountToSell = tokenAmount;
        tokenAmount = minVal(tokenAmount, balanceOf(msg.sender));
        
        require(tokenAmount > 0, "Token amount can not be 0");
        require(getTokenAvailableToSellTodayUser(msg.sender, tokenAmount), "Daily Sell Limit exceed. Max 10.000 tokens daily.");
        require(sold[getCurrentDay()].add(tokenAmount) <= SELL_LIMIT, "Daily Sell Limit exceed");
        sold[getCurrentDay()] = sold[getCurrentDay()].add(tokenAmount);

        uint BUSDAmount = tokenToBUSD(tokenAmount);
        require(getContractBUSDBalance() > BUSDAmount, "Insufficient Contract Balance");

        if (users[msg.sender].lastTimeSold == 0) {
            users[msg.sender].lastTimeSold = now;
        }
        _burn(msg.sender, tokenAmount);
       token.transfer(msg.sender, BUSDAmount);
        emit TokenOperation(msg.sender, "SELL", tokenAmount, BUSDAmount);
    }   
      

    function getUserUnclaimedTokens_M(address _addr) public view returns(uint value) {
        User storage user = users[_addr];
        return getStakeBUSD_IP(_addr).add(user.sM.unClaimedTokens); 
    }
    
    function getUserUnclaimedTokens_T(address _addr) public view returns(uint value) {
        User storage user = users[_addr];
        return getStakeToken_IP(_addr).add(user.sT.unClaimedTokens); 
    }   
	
    function getUserTimeToNextAirdrop(address _addr) public view returns (uint) {
        //return minZero(users[_addr].lastAirdrop.add(NEXT_AIRDROP), now);
    }    
    
	function getContractBUSDBalance() public view returns (uint) {
	    // return address(this).balance;
	    return token.balanceOf(address(this));
	}  
	
	function getContractTokenBalance() public view returns (uint) {
		return balanceOf(address(this));
	}  
	
	function getAPY_M() public view returns (uint) {
		return BUSD_DAILYPROFIT.mul(365).div(10);
	}
	
	
	function getUserBUSDBalance(address _addr) public view returns (uint) {
		return address(_addr).balance;
	}	
	
	function getUserTokenBalance(address _addr) public view returns (uint) {
		return balanceOf(_addr);
	}

    function getUserTokensAccumulated() public view returns (uint) {
		return getUserTokenBalance(msg.sender).add(getUserTokenStaked(msg.sender));
	}	
    function getUserReferrer(address _addr) public view returns (address){
        return users[_addr].referrer;
    }
	
	function getUserBUSDStaked(address _addr) public view returns (uint) {
		return users[_addr].sM.totalStaked;
	}	
	
	function getUserTokenStaked(address _addr) public view returns (uint) {
		return users[_addr].sT.totalStaked;
	}
	
    function getTokenPrice() public view returns(uint value) {
        uint d1 = getContractBUSDBalance().mul(PRICE_DIVIDER);
        uint d2 = availableSupply().add(1);
        if(d1.div(d2) > 1 ether){
            value = LIMIT_TOKEN_PRICE;
        }else{
            value = d1.div(d2);
        }
        return value;
    } 

    function BUSDToToken(uint BUSDAmount) public view returns(uint) {
        return BUSDAmount.mul(PRICE_DIVIDER).div(getTokenPrice());
    }

    function tokenToBUSD(uint tokenAmount) public view returns(uint) {
        return tokenAmount.mul(getTokenPrice()).div(PRICE_DIVIDER);
    } 	

    function addStake(uint _index, address _addr, uint _amount, uint _period, uint _startTime) private {
    Staking memory newStaking;
    newStaking.id = _index;
    newStaking.addr = _addr;
    newStaking.amount = _amount;
    newStaking.period = StakeType(_period);
    newStaking.startTimeStake = _startTime;
    stakings.push(newStaking);
  }
    
    function findStakings(address addr) public view returns(Staking[] memory) { 
    uint256 resultCount;
    for (uint i = 0; i < stakings.length; i++) {
        if (stakings[i].addr == addr) {
            resultCount++;
        }
    }
    Staking[] memory result = new Staking[](resultCount);
    uint256 j;
    for (uint i = 0; i < stakings.length; i++) {
        if (stakings[i].addr == addr) {
            result[j] = stakings[i];
            j++;
        }
    }
    return result;
    }
    
	function getContractLaunchTime() public view returns(uint) {
		return minZero(startTime, block.timestamp);
	}
	
    function getCurrentDay() public view returns (uint) {
        return minZero(now, startTime).div(TIME_STEP);
    }	
    
    function getTokenSoldToday() public view returns (uint) {
        return sold[getCurrentDay()];
    }  

    function getTokenSoldTodaybyUser(address userAddress) public view returns (uint) {
        return users[userAddress].dailySell;
    }   
    
    function getTokenAvailableToSell() public view returns (uint) {
       return minZero(SELL_LIMIT, sold[getCurrentDay()]);
    }  

    function getTokenAvailableToSellTodayUser(address userAddress, uint amountSelling) private returns (bool) {
        uint amount = users[userAddress].dailySell+amountSelling;
        if(amount <= SELL_LIMIT_DAILY_USER){
            users[msg.sender].dailySell = users[msg.sender].dailySell + amountSelling;
            return true;
        }else if(now >= users[userAddress].lastTimeSold+5 minutes && amount <= SELL_LIMIT_DAILY_USER){
            users[userAddress].dailySell = amountSelling;
            users[userAddress].lastTimeSold = now;
            return true;
        }else{
            return false;
        }
    } 

    
    function getTimeToNextDay() public view returns (uint) {
        uint t = minZero(now, startTime);
        uint g = getCurrentDay().mul(TIME_STEP);
        return g.add(TIME_STEP).sub(t);
    }    

    function checkStakeTime(uint _id) private view returns(bool _success){
        if (stakings[_id].period == StakeType.EASY) {
            _success = true;
        }else if(stakings[_id].period == StakeType.MEDIUM && stakings[_id].startTimeStake.add(TIME_TO_UNSTAKE_M) < now){
            _success = false;
        }else if(stakings[_id].period == StakeType.HARD && stakings[_id].startTimeStake.add(TIME_TO_UNSTAKE_H) < now){
            _success = false;
        }
    return _success;
    }
     
    
    function minZero(uint a, uint b) private pure returns(uint) {
        if (a > b) {
           return a - b; 
        } else {
           return 0;    
        }    
    }   
    
    function maxVal(uint a, uint b) private pure returns(uint) {
        if (a > b) {
           return a; 
        } else {
           return b;    
        }    
    }
    
    function minVal(uint a, uint b) private pure returns(uint) {
        if (a > b) {
           return b; 
        } else {
           return a;    
        }    
    }    
}
